ogleplus="" instagram="" linkedin="" myspace="" paypal="" pinterest="" reddit="" rss="" skype="" soundcloud="" spotify="" tumblr="" twitter="" vimeo="" vk="" xing="" yahoo="" yelp="" youtube="" email="" show_custom="no" hide_on_mobile="small-visibility,medium-visibility,large-visibility" class="" id="" /][/fusion_builder_column][fusion_builder_column type="1_4" layout="1_1" spacing="1%" center_content="no" hover_type="none" link="" min_height="" hide_on_mobile="no" class="" id="" background_color="rgba(3,169,244,0)" background_image="" background_position="center center" undefined="" background_repeat="no-repeat" border_size="0" border_color="" border_style="solid" border_position="all" padding="10px 0% 10px 0%" margin_top="0px" margin_bottom="0px" animation_type="zoom" animation_direction="down" animation_speed="0.7" animation_offset="top-into-view" last="no"][fusion_person name="Nguyễn Lê Vi Huỳnh" title="Phó Trưởng Phòng Cơ sở Dữ liệu" picture="" pic_link="" linktarget="_self" pic_style="glow" hover_type="zoomin" background_color="rgba(255,255,255,0)" content_alignment="center" pic_style_color="#ffffff" pic_bordersize="" pic_bordercolor="" pic_borderradius="" icon_position="" social_icon_boxed="" social_icon_boxed_radius="30px" social_icon_color_type="" social_icon_colors="#fff500" social_icon_boxed_colors="#0093dd" social_icon_tooltip="" blogger="" deviantart="" digg="" dribbble="" dropbox="" facebook="" flickr="" forrst="" googleplus="" instagram="" linkedin="" myspace="" paypal="" pinterest="" reddit="" rss="" skype="" soundcloud="" spotify="" tumblr="" twitter="" vimeo="" vk="" xing="" yahoo="" yelp="" youtube="" email="" show_custom="no" hide_on_mobile="small-visibility,medium-visibility,large-visibility" class="" id="" /][/fusion_builder_column][/fusion_builder_row][/fusion_builder_container][fusion_builder_container admin_label="Đội ngũ nhân viên" hundred_percent="no" equal_height_columns="no" menu_anchor="" hide_on_mobile="no" class="" id="" background_color="" background_image="http://hcmgis.vn/wp-content/uploads/2015/10/trung_tam.jpg" background_position="center bottom" background_repeat="no-repeat" fade="no" background_parallax="none" enable_mobile="no" parallax_speed="0.3" video_mp4="" video_webm="" video_ogv="" video_url="" video_aspect_ratio="16:9" video_loop="yes" video_mute="yes" video_preview_image="" border_size="0px" border_color="" border_style="solid" margin_top="" margin_bottom="" padding_top="0px" padding_right="" padding_bottom="0px" padding_left=""][fusion_builder_row][fusion_builder_column type="1_1" layout="1_1" spacing="yes" center_content="no" hover_type="none" link="" min_height="" hide_on_mobile="no" class="" id="" background_color="" background_image="" background_position="center center" undefined="" background_repeat="no-repeat" border_size="0" border_color="" border_style="solid" border_position="all" padding="4% 4% 60% 4%" margin_top="" margin_bottom="" animation_type="fade" animation_direction="left" animation_speed="0.6" animation_offset="" last="no"][fusion_text]\r\n<div style="background-color: rgba(0, 116, 170, 0.76); padding: 10px;">\r\n<p style="font-size: 30px; letter-spacing: 6px;"><span style="color: #ffffff;"><strong>ĐỘI NGŨ NHÂN VIÊN</strong></span></p>\r\n<p style="font-size: 16px; color: #ffffff;">Trung tâm có đội ngũ nhân viên với trên 30 kỹ sư và cử nhân được đào tạo bài bản từ các trường Đại học hàng đầu trong và ngoài nước, trong đó có nhiều người có trình độ trên đại học. Nhân lực của Trung tâm là sự kết hợp giữa những người trẻ năng động, nhiệt huyết và những người có nhiều kinh nghiệm, đi tiên phong trong quá trình ứng dụng GIS tại TP.Hồ Chí Minh và Việt Nam. Ngoài ra, Trung tâm còn có đông đảo đội ngũ cộng tác viên là các chuyên gia giỏi của các sở ngành, trường, viện, công ty hoạt động trong lĩnh vực CNTT, GIS và các lĩnh vực liên quan.</p>\r\n\r\n</div>\r\n[/fusion_text][/fusion_builder_column][/fusion_builder_row][/fusion_builder_container][fusion_builder_container admin_label="lien hệ" hundred_percent="yes" equal_height_columns="no" menu_anchor="" hide_on_mobile="no" class="" id="" background_color="#00bcd4" background_image="" background_position="left top" background_repeat="no-repeat" fade="no" background_parallax="none" enable_mobile="no" parallax_speed="0.3" video_mp4="" video_webm="" video_ogv="" video_url="" video_aspect_ratio="16:9" video_loop="yes" video_mute="yes" video_preview_image="" border_size="0px" border_color="" border_style="solid" margin_top="" margin_bottom="" padding_top="0" padding_right="0" padding_bottom="0" padding_left="0" admin_toggled="no"][fusion_builder_row][fusion_builder_column type="2_3" layout="1_1" spacing="yes" center_content="no" hover_type="none" link="" min_height="none" hide_on_mobile="no" class="" id="" background_color="" background_image="" background_position="left top" undefined="" background_repeat="no-repeat" border_size="4" border_color="#00bcd4" border_style="solid" border_position="top" padding="" margin_top="0px" margin_bottom="0px" animation_type="" animation_direction="left" animation_speed="0.3" animation_offset="" last="no"][fusion_map address="244 Điện Biên Phủ, Phường 7, Quận 3, Hồ Chí Minh" type="roadmap" width="100%" height="460px" zoom="14" scrollwheel="no" scale="yes" zoom_pancontrol="yes" animation="yes" popup="no" map_style="custom" overlay_color="#7ccdff" infobox_content="Trung tâm Ứng dụng Hệ Thống Thông tin Địa lý\r\n244 Điện Biên Phủ, Phường 7, Quận 3, Hồ Chí Minh" infobox="default" infobox_text_color="" infobox_background_color="#ffffff" icon="http://i.imgur.com/ML9KqIu.png" hide_on_mobile="small-visibility,medium-visibility,large-visibility" class="" id="" /][/fusion_builder_column][fusion_builder_column type="1_3" layout="1_3" spacing="" center_content="no" hover_type="none" link="" min_height="" hide_on_mobile="small-visibility,medium-visibility,large-visibility" class="" id="" background_color="" background_image="" background_position="left top" background_repeat="no-repeat" border_size="0" border_color="" border_style="solid" border_position="all" padding="" dimension_margin="" animation_type="" animation_direction="left" animation_speed="0.3" animation_offset="" last="no"][fusion_text]\r\n<div class="fusion-column-wrapper" style="padding: 0px 0px 0px 20px; background-position: left top; background-repeat: no-repeat; -webkit-background-size: cover; -moz-background-size: cover; -o-background-size: cover; background-size: cover;" data-bg-url="">\r\n<div class="fusion-sep-clear"></div>\r\n<div class="fusion-separator sep-none" style="width: 100%; max-width: 60%; margin: 0px auto 34px auto;"></div>\r\n<div class="fusion-title title fusion-sep-none fusion-title-size-three fusion-border-below-title" style="margin-top: 0px; margin-bottom: -4px; margin-right: 10%;">\r\n<div><span style="color: #ffffff; font-weight: bold; font-size: 25px;">Trung tâm Ứng dụng Hệ thống Thông tin Địa lý Thành phố Hồ Chí Minh</span></div>\r\n</div>\r\n<div class="fusion-sep-clear"></div>\r\n<div class="fusion-separator sep-single" style="border-color: #ffffff; border-top-width: 1px; margin-top: 30px; margin-bottom: 50px; width: 100%; max-width: 8%;"></div>\r\n<p style="color: #ffffff; font-size: 18px;">244 Điện Biên Phủ, phường 7, quận 3, Hồ Chí Minh</p>\r\n<p style="color: #ffffff; font-size: 18px;">Phone: 028.3932.0963\r\nEmail: <a style="color: rgba(255,255,255,.6);" href="mailto:gis.skhcn@tphcm.gov.vn" target="_blank" rel="noopener">gis.skhcn@tphcm.gov.vn\r\n</a>Web: hcmgis.vn</p>\r\n\r\n<div class="fusion-sep-clear"></div>\r\n<div class="fusion-separator sep-none" style="margin-top: 30px; margin-bottom: 30px; width: 100%; max-width: 60%;"></div>\r\n<div class="fusion-clearfix"></div>\r\n</div>\r\n[/fusion_text][/fusion_builder_column][/fusion_builder_row][/fusion_builder_container]', 'Giới thiệu', '', 'inherit', 'closed', 'closed', '', '881-revision-v1', '', '', '2018-03-28 15:48:25', '2018-03-28 08:48:25', '', 881, 'http://hcmgis.vn/2018/03/28/881-revision-v1/', 0, 'revision', '', 0),
	(2000, 1, '2018-03-29 16:54:41', '2018-03-29 09:54:41', '', 'QGIS', '', 'inherit', 'open', 'closed', '', 'qgis', '', '', '2018-03-29 16:54:41', '2018-03-29 09:54:41', '', 1984, 'http://hcmgis.vn/wp-content/uploads/2018/03/QGIS.png', 0, 'attachment', 'image/png', 0),
	(2002, 1, '2018-03-29 16:56:23', '2018-03-29 09:56:23', '<img class="wp-image-1987 aligncenter" src="http://hcmgis.vn/wp-content/uploads/2018/03/HCMGISQGIS3.png" alt="" width="958" height="514" />\r\n\r\nHCMGIS là QGIS3 plugin cung cấp một số công cụ xử lý thường dùng trên dữ liệu vector phục vụ cộng đồng người dùng Việt Nam:\r\n- Xử lý không gian: gộp layers, tách layer, kiểm tra lỗi hình học, chỉnh sửa lỗi hình học, chuyển đổi hệ tọa độ.\r\n- Xử lý thuộc tính: gộp/ tách trường dữ liệu, thêm tiền tố/ hậu tố, find &amp; INSERT. Đặc biệt là chức năng chuyển đổi font chữ tiếng Việt Unicode &lt;--&gt; TCVN3 &lt;--&gt; Vni-Windows, với các tiện ích IN HOA, Hoa Mỗi Từ, Hoa đầu câu, in thường, khong dau.\r\n\r\nXem HCMGIS trên QGIS Plugins: <a href="https://plugins.qgis.org/plugins/HCMGIS/">https://plugins.qgis.org/plugins/HCMGIS/</a>\r\n\r\nCài đặt: trong giao diện QGIS, vào Plugins/Manage and Install Plugins, search HCMGIS và chọn Install Plugin\r\n\r\n<img class="size-full wp-image-2000 aligncenter" src="http://hcmgis.vn/wp-content/uploads/2018/03/QGIS.png" alt="" width="1041" height="567" />', 'HCMGIS plugin for QGIS3', '', 'inherit', 'closed', 'closed', '', '1984-revision-v1', '', '', '2018-03-29 16:56:23', '2018-03-29 09:56:23', '', 1984, 'http://hcmgis.vn/2018/03/29/1984-revision-v1/', 0, 'revision', '', 0),
	(2003, 1, '2018-03-29 16:57:19', '2018-03-29 09:57:19', '', 'HCMGIS plugin for QGIS3', '', 'inherit', 'closed', 'closed', '', '1984-revision-v1', '', '', '2018-03-29 16:57:19', '2018-03-29 09:57:19', '', 1984, 'http://hcmgis.vn/2018/03/29/1984-revision-v1/', 0, 'revision', '', 0),
