	(4097, 1, '2018-12-04 11:27:40', '2018-12-04 04:27:40', '[pdfviewer width="100%" height="849px" beta="false"]https://hcmgis.vn/wp-content/uploads/2018/10/ThongBao_KetQuaDiemXetTuyenVienChucNam2018.pdf[/pdfviewer]\r\n\r\nĐơn xin phúc khảo:\r\n\r\n[dg ids="3650"]', 'Thông báo kết quả xét tuyển viên chức năm 2018', '', 'inherit', 'closed', 'closed', '', '4096-revision-v1', '', '', '2018-12-04 11:27:40', '2018-12-04 04:27:40', '', 4096, 'https://hcmgis.vn/trang-chu/4096-revision-v1/', 0, 'revision', '', 0),
